mapper.select:
select

from company
where company_id = #{company_id,jdbcType=VARCHAR}
AND company_jobs = #{company_jobs,jdbcType=VARCHAR}

mapper.delete:
delete from company
where company_id = #{company_id,jdbcType=VARCHAR}
AND company_jobs = #{company_jobs,jdbcType=VARCHAR}

mapper.update:
update company

	
		company_name = #{company_name,jdbcType=VARCHAR},
	

where company_id = #{company_id,jdbcType=VARCHAR}
AND company_jobs = #{company_jobs,jdbcType=VARCHAR}

update company
set company_name = #{company_name,jdbcType=VARCHAR}
where company_id = #{company_id,jdbcType=VARCHAR}
AND company_jobs = #{company_jobs,jdbcType=VARCHAR}
