mapper.select:
select

from course
where course_id = #{course_id,jdbcType=VARCHAR}
AND course_teacher = #{course_teacher,jdbcType=VARCHAR}

mapper.delete:
delete from course
where course_id = #{course_id,jdbcType=VARCHAR}
AND course_teacher = #{course_teacher,jdbcType=VARCHAR}

mapper.update:
update course
set course_pre_id=#{course_pre_id,jdbcType=VARCHAR},
course_name = #{course_name,jdbcType=VARCHAR},
course_type = #{course_type,jdbcType=VARCHAR},
course_credits = #{course_credits,jdbcType=INTEGER}
where course_id = #{course_id,jdbcType=VARCHAR}
AND course_teacher = #{course_teacher,jdbcType=VARCHAR}
