mapper.select:
select

from personal
where personal_student = #{personal_student,jdbcType=VARCHAR} 
AND personal_course_id = #{personal_course_id,jdbcType=VARCHAR}

mapper.delete:
delete from personal
where personal_student = #{personal_student,jdbcType=VARCHAR}
AND personal_course_id = #{personal_course_id,jdbcType=VARCHAR}

mapper.update:
update personal

	
		personal_course = #{personal_course,jdbcType=VARCHAR},
	
	
		personal_teacher = #{personal_teacher,jdbcType=VARCHAR},
	
	

where personal_student = #{personal_student,jdbcType=VARCHAR}
AND personal_course_id = #{personal_course_id,jdbcType=VARCHAR}

update personal
set personal_course = #{personal_course,jdbcType=VARCHAR},
personal_teacher = #{personal_teacher,jdbcType=VARCHAR}
where personal_student = #{personal_student,jdbcType=VARCHAR}
AND personal_course_id = #{personal_course_id,jdbcType=VARCHAR}
