mapper.select:
select

from results
where results_student = #{results_student,jdbcType=VARCHAR} 
AND results_course = #{results_course,jdbcType=VARCHAR}

mapper.delete:
delete from results
where results_student = #{results_student,jdbcType=VARCHAR}
AND results_course = #{results_course,jdbcType=VARCHAR}

mapper.update:
update results

	
		results_credits = #{results_credits,jdbcType=INTEGER},
	
	
		result_score = #{result_score,jdbcType=Float},
	
	

where results_student = #{results_student,jdbcType=VARCHAR}
AND results_course = #{results_course,jdbcType=VARCHAR}

update results
set results_credits = #{results_credits,jdbcType=INTEGER},
result_score = #{result_score,jdbcType=Float}
where results_student = #{results_student,jdbcType=VARCHAR}
AND results_course = #{results_course,jdbcType=VARCHAR}
